# Створення множини за допомогою фігурних дужок my_set = {1, 2, 3, "apple", "banana"}
# Створення множини за допомогою функції set() my_set2 = set([4, 5, 6, "orange", "grape"])
print(my_set) print(my_set2)

my_set = {1, 2, 3}
my_set2 = {3, 4, 5}

# Додавання елемента до множини
my_set.add(4)
print(my_set)  # Виведе: {1, 2, 3, 4}

# Видалення елемента з множини
my_set.remove(2)
print(my_set)  # Виведе: {1, 3, 4}

# Об'єднання множин
union_set = my_set.union(my_set2)
print(union_set)  # Виведе: {1, 3, 4, 5}

# Перетин множин
intersection_set = my_set.intersection(my_set2)
print(intersection_set)  # Виведе: {3, 4}

my_set = {1, 2, 3}

# Метод add() додає елемент до множини
my_set.add(4)
print(my_set)  # Виведе: {1, 2, 3, 4}

# Метод remove() видаляє елемент з множини
my_set.remove(2)
print(my_set)  # Виведе: {1, 3, 4}

# Метод union() повертає об'єднання множин
my_set2 = {3, 4, 5}
union_set = my_set.union(my_set2)
print(union_set)  # Виведе: {1, 3, 4, 5}

# Метод intersection() повертає перетин множин
intersection_set = my_set.intersection(my_set2)
print(intersection_set)  # Виведе: {3, 4}
